import React, { Component } from 'react';

import { connect } from 'react-redux';
import logo from 'assets/images/logo.png';
import firebase from 'lib/firebase';
import {
  Container,
  Divider,
  Dropdown,
  Button,
  Grid,
  Header,
  Image,
  List,
  Menu,
  Segment
} from 'semantic-ui-react';

class App extends Component {
  handleSignOut = () => {
    firebase
      .auth()
      .signOut()
      .then(() => console.log('signed out'));
  };

  render() {
    const {} = this.props;

    return (
      <div className="">
        <Menu inverted>
          <Menu.Item header>
            <Image size="mini" src={logo} style={{ marginRight: '1.5em' }} />
            UofTHacksVI
          </Menu.Item>

          <Menu.Item position="right">
            <Button as="a" inverted onClick={this.handleSignOut}>
              Log Out
            </Button>
          </Menu.Item>
        </Menu>

        <Container text style={{ marginTop: '7em' }} />

        <Segment
          inverted
          vertical
          style={{ margin: '5em 0em 0em', padding: '2em 2em' }}
        >
          <Container textAlign="center">
            <Image centered size="mini" src={logo} />
            <Divider inverted section />
            <List horizontal inverted divided link size="small">
              <List.Item as="a" href="#">
                Contact Us
              </List.Item>
              <List.Item as="a" href="#">
                Terms and Conditions
              </List.Item>
              <List.Item as="a" href="#">
                Privacy Policy
              </List.Item>
            </List>
          </Container>
        </Segment>
      </div>
    );
  }
}

export default connect(({ user, channel }) => {
  const { currentUser } = user;
  const { currentChannel, isPrivateChannel, userPosts } = channel;

  return {
    currentUser,
    currentChannel,
    isPrivateChannel,
    userPosts
  };
})(App);
