import React, { Component } from 'react';

import firebase from 'lib/firebase';
import { Chart } from 'react-charts';

class Progress extends Component {
  state = { usersRef: firebase.database().ref('users'), loadedDates: [] };

  componentDidMount() {
    const { currentUser } = this.props;

    if (currentUser) {
      this.addListeners(currentUser.uid);
    }
  }

  addListeners = currentUserId => {
    let loadedDates = [];
    console.log(currentUserId);
    this.state.usersRef
      .child(currentUserId)
      .child('dates')
      .on('child_added', snap => {
        loadedDates.push(snap.val());
        console.log(loadedDates);

        // let result = loadedDates.map(item => (
        //   if (true)
        //    item.correct / item.total / item.time;
        // ));
        // this.setState({ messages: loadedMessages, messagesLoading: false });
        // this.countUniqueUsers(loadedMessages);
        // this.countUserPosts(loadedMessages);
        this.setChartData(loadedDates);
      });

    // this.setState({ loadedDates: result });
  };

  setChartData = loadedData => {
    let result = loadedData.map(item => {
      console.log('1');
      let score = item.correct / item.total / item.time;
      return [item.date, score];
    });
    console.log(result);
  };

  render() {
    const { loadedDates } = this.state;
    return (
      <Chart
        style={{
          width: '400px',
          height: '300px'
        }}
        data={[
          {
            label: 'Progress',
            data: [[0, 1], [1, 2], [2, 4], [3, 2], [4, 7]]
          }
        ]}
        axes={[
          { primary: true, type: 'linear', position: 'bottom' },
          { type: 'linear', position: 'left' }
        ]}
      />
    );
  }
}

export default Progress;
