import React, { Component } from 'react';

import mime from 'mime-types';
import { Modal, Input, Button, Icon, Form } from 'semantic-ui-react';

class FundModal extends Component {
  state = { name: '', amount: '', email: '' };
  sendMoneyRequest = () => {
    const { file } = this.state;
    const { uploadFile, closeModal } = this.props;

    if (file && this.isAuthorized(file.name)) {
      const metaData = { contentType: mime.lookup(file.name) };
      uploadFile(file, metaData);
      closeModal();
      this.clearFile();
    }
  };

  handleChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  clearFile = () => {
    this.setState({ file: null });
  };

  render() {
    const { open, closeModal } = this.props;
    return (
      <Modal basic open={open} onClose={closeModal}>
        <Modal.Header>Fund raising</Modal.Header>
        <Modal.Content>
          <Form onSubmit={this.handleSubmit}>
            <Form.Field>
              <Input
                fluid
                label="Name"
                name="name"
                onChange={this.handleChange}
              />
            </Form.Field>

            <Form.Field>
              <Input
                fluid
                label="Email"
                name="email"
                onChange={this.handleChange}
              />
            </Form.Field>

            <Form.Field>
              <Input
                fluid
                label="Amount"
                name="amount"
                onChange={this.handleChange}
                type="number"
              />
            </Form.Field>
          </Form>
        </Modal.Content>
        <Modal.Actions>
          <Button color="green" inverted onClick={this.handleSubmit}>
            <Icon name="checkmark" /> Add
          </Button>
          <Button color="red" inverted onClick={closeModal}>
            <Icon name="remove" /> Cancel
          </Button>
        </Modal.Actions>
      </Modal>
    );
  }
}

export default FundModal;
